import React, {useState} from 'react';
import { Text, View, TextInput, Button, StyleSheet, Image} from 'react-native';
import { styles } from '../../Style/style1';
import Informacao from './Informacao';
import Imc from './Imc';

export default function Content(){
    return(
        <View style={styles.content}>

            <View style={styles.content1}> 
                <Text style={styles.textContent}>Seja Bem Vindo ao Soul Health</Text>
                <Informacao boasvindas='Informamos sobre massagens, técnicas de meditação e tudo o que pode fazer para promover o bem-estar, melhorando sua qualidade de vida e auto-estima, com dicas práticas e simples, para vencer as dificuldades da vida e assim viver mais feliz e confiante.'/> 
                <Image style={styles.imgContent} source={require('../../img/silhueta.png')}/>
            </View>

            <View>
                    <Text style={styles.textContent}>Notícias</Text>
                    <Informacao style={styles.linhaContent2} titulo={`7 dicas para controlar a ansiedade e o nervosismo \n `} autor={`Dr. Arthur Frazão \n `} conteudo='A ansiedade pode gerar sintomas físicos e psicológicos, como sensação de falta de ar, aperto no peito, tremores ou pensamentos negativos, por exemplo, que podem condicionar o dia-a-dia da pessoa e aumentar o risco de contrair doenças.'/>

                    <Informacao style={styles.linhaContent} titulo={`6 dicas para sair do sedentarismo \n `} autor={`Carlos Bruce \n `}conteudo='Para sair do sedentarismo deve-se começar aos poucos e criar metas possíveis e alcançáveis para não perder a motivação, e para isso, pode-se começar com pequenas mudanças nos hábitos de vida como reduzir o uso do carro ou substituir o elevador pelas escadas, além de estabelecer uma rotina com um horário específico para cuidar da saúde e movimentar o corpo.'/>

                    <Informacao style={styles.linhaContent} titulo={`Contém Glúten \n ` } autor={`Tiago Dantas \n `} conteudo='O glúten é uma proteína presente no trigo e seus derivados. É importante na fabricação de pães, pois é a proteína que dá liga à massa, além de ajudar o pão a crescer. A farinha de trigo é a maior fonte de glúten para a nossa alimentação.'/>
            </View>
        </View>
    )    
}
